    Axios.get("http://localhost:8000/api/products").then((data) => {
      // console.log(data.data);
      setProductList(data.data);
    });
  }, []);

  const onAdd = (product) => {
    // console.log(product);
    const exist = cartItems.find((x) => x.id === product.id);
    // console.log(exist);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === product.id ? { ...exist, qty: exist.qty + 1 } : x
