    setCart([...cart, "fuck"]);
    console.log(cart);
    console.log(cart.length)
    console.log($(".productsBuyTitleh3")[0].innerText)
    console.log($(".cartItemProductsText")[0].innerText)
    // let x = $(".cartItemProductsText")[cart.length]
