    $(document).ready(function () {
      $(".fa-cart-plus").on("click", function () {
        $(".productsBuy").css("display", "flex");
        $(".productsBuyItem").css("display", "block");
      });
      $(".productsItemCart").on("click", function () {
        $(".productsBuy").css("display", "flex");
        $(".productsBuyItem").css("display", "block");
      });
      $(".productsBuyClose").on("click", function () {
